import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
import { Link } from "react-router-dom";

const Flight = (props) => (
  <tr>
    <td>{props.flight.flight_from}</td>
    <td>{props.flight.flight_to}</td>
    <td>{props.flight.flight_date}</td>
    <td>{props.flight.flight_cabin}</td>
    <td>{props.flight.flight_seats}</td>
    <td>
      <Link to={"/edit/" + props.flight._id}>Edit</Link> |
      <a
        href="/"
        onClick={() => {
          props.deleteFlight(props.flight._id);
        }}
      >
        Delete
      </a>
    </td>
  </tr>
);

export default class FlightList extends Component {
  // This is the constructor that shall store our data retrieved from the database
  constructor(props) {
    super(props);
    this.deleteFlight = this.deleteFlight.bind(this);
    this.state = { flights: [] };
  }

  // This method will get the data from the database.
  componentDidMount() {
    axios
      .get("http://localhost:5000/flight/")
      .then((response) => {
        this.setState({ flights: response.data });
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  // This method will delete a flight based on the method
  deleteFlight(id) {
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      flight: this.state.flights.filter((el) => el._id !== id),
    });
  }

  // This method will map out the users on the table
  flightList() {
    return this.state.flights.map((currentflight) => {
      return (
        <Flight
          flight={currentflight}
          deleteFlight={this.deleteFlight}
          key={currentflight._id}
        />
      );
    });
  }

  // This following section will display the table with the flights of individuals.
  render() {
    return (
      <div>
        <h3>Flight List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>From</th>
              <th>To</th>
              <th>Flight Date</th>
              <th>Cabin</th>
              <th>Seats Available</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>{this.flightList()}</tbody>
        </table>
      </div>
    );
  }
}
